
open (INFILE, "<", $ARGV[0]);
open (OUTFILE, ">", $ARGV[1]);


$a = 1;
$count = 0;


#my $DNAGI1NORWALK = 
#"";

my $ORF1 =
"*";

my $range1 = $ARGV[2];
my $range2 = $ARGV[3];

# while loop that reads into INFILE
while (my $line = <INFILE>) {
	#chomp($line);
	$line =~ s/\R//g;
	($chr1_c1, $start_c2, $end_c3) = split /\t/, $line;		
	if (($start_c2 > $range1)&&($start_c2 < 10361)&&($end_c3 < $range2)) {
		my $extractpep = substr($ORF1, ((($start_c2 + 3) - 4995)/3), ((($end_c3 - $start_c2) + 1)/3));
		print ">$a\n";
		#print "$extractseq\n";
		print "$extractpep\n";
		print OUTFILE ">$a\n";
		#print OUTFILE "$extractseq\n";
		print OUTFILE "$extractpep\n";
		$a++;
		$count++;
	}
}


print "count: $count\n";


close INFILE;
close OUTFILE;




